INSERT INTO `web_vul_list` VALUES ('109', '109', 'phpwind 7.5 “apps/groups/index.php”远程包含漏洞', '1', '系统命令执行', '命令执行类型', '2', 'PHPwindRmomteIncludeScript', 'HIGH', 'PHPWind（简称：PW）是一个基于PHP和MySQL的论坛程序，是国内最受欢迎的通用型论坛程序之一。\r\napps/groups/index.php 里$route和$basePath变量没有初始化,导致远程包含或者本地包含php文件,导致执行任意php代码。\r\n\r\n<?php\r\nif ($route == \"groups\") {\r\nrequire_once $basePath . \'/action/m_groups.php\';\r\n} elseif ($route == \"group\") {\r\nrequire_once $basePath . \'/action/m_group.php\';\r\n} elseif ($route == \"galbum\") {\r\nrequire_once $basePath . \'/action/m_galbum.php\';\r\n}', '补丁地址：\r\nhttp://www.phpwind.net/read-htm-tid-914851.html\r\n\r\n<?php\r\n!function_exists(\'readover\') && exit(\'Forbidden\');\r\nif ($route == \"groups\") {\r\nrequire_once $basePath . \'/action/m_groups.php\';\r\n} elseif ($route == \"group\") {\r\nrequire_once $basePath . \'/action/m_group.php\';\r\n} elseif ($route == \"galbum\") {\r\nrequire_once $basePath . \'/action/m_galbum.php\';\r\n}\r\n?>', '0', '5');
