INSERT INTO `web_vul_list` VALUES ('168', '168', 'DedeCms V5.7 \"plus/search.php\" SQL注入', '1', 'SQL注入', '命令执行类型', '2', 'DeDeCms_v57SqlInjectionScript', 'HIGH', 'DeDeCMS（织梦CMS）是一款国内比较专业的PHP网站内容管理系统,该系统5.7版本存在严重的SQL注入漏洞。\r\n漏洞源码分析：\r\n\r\nrequire_once(dirname(__FILE__).”/../include/common.inc.php”); \r\nrequire_once(DEDEINC.”/arc.searchview.class.php”); \r\n\r\n$pagesize = (isset($pagesize) && is_numeric($pagesize)) ? $pagesize : 10; \r\n$typeid = (isset($typeid) && is_numeric($typeid)) ? $typeid : 0; \r\n$channeltype = (isset($channeltype) && is_numeric($channeltype)) ? $channeltype : 0; \r\n$kwtype = (isset($kwtype) && is_numeric($kwtype)) ? $kwtype : 1; \r\n$mid = (isset($mid) && is_numeric($mid)) ? $mid : 0; \r\n\r\nif(!isset($orderby)) $orderby=”; \r\nelse $orderby = preg_replace(“#[^a-z]#i”, ”, $orderby); \r\n\r\n\r\nif(!isset($searchtype)) $searchtype = ‘titlekeyword’; \r\nelse $searchtype = preg_replace(“#[^a-z]#i”, ”, $searchtype); \r\n\r\nif(!isset($keyword)){ \r\n    if(!isset($q)) $q = ”; \r\n    $keyword=$q; \r\n} \r\n\r\n$oldkeyword = $keyword = FilterSearch(stripslashes($keyword)); \r\n\r\n//查找栏目信息 \r\nif(empty($typeid)) \r\n{ \r\n    $typenameCacheFile = DEDEDATA.’/cache/typename.inc’; \r\n    if(!file_exists($typenameCacheFile) || filemtime($typenameCacheFile) < time()-(3600*24) ) \r\n    { \r\n        $fp = fopen(DEDEDATA.’/cache/typename.inc’, ‘w’); \r\n        fwrite($fp, “<”.”?php\\r\\n”); \r\n        $dsql->SetQuery(“Select id,typename,channeltype From `#@__arctype`”); \r\n        $dsql->Execute(); \r\n        while($row = $dsql->GetArray()) \r\n        { \r\n            fwrite($fp, “\\$typeArr[{$row[\'id\']}] = ‘{$row[\'typename\']}’;\\r\\n”); \r\n        } \r\n        fwrite($fp, ‘?’.\'>’); \r\n        fclose($fp); \r\n    } \r\n    //引入栏目缓存并看关键字是否有相关栏目内容 \r\n    require_once($typenameCacheFile); \r\n//$typeArr这个数组是包含生成的临时文件 里面定义的,由于dedecms的全局变量机制,我们可以自己定义一个 \r\n// \r\n    if(isset($typeArr) && is_array($typeArr)) \r\n    { \r\n        foreach($typeArr as $id=>$typename) \r\n        { \r\n\r\n            <font color=”Red”>$keywordn = str_replace($typename, ‘ ‘, $keyword);</font>  //这个地方要绕过 \r\n            if($keyword != $keywordn) \r\n            { \r\n                $keyword = $keywordn; \r\n                <font color=”Red”>$typeid = $id; </font>// 这里存在变量覆盖漏洞使 $typeid = (isset($typeid) && is_numeric($typeid)) ? $typeid : 0; 这句过滤成了摆设 \r\n                break; \r\n            } \r\n        } \r\n    } \r\n} \r\n然后plus/search.php文件下面定义了一个 Search类的对象 .\r\n在arc.searchview.class.php 文件的SearchView类的构造函数 声明了一个TypeLink类.\r\n$this->TypeLink = new TypeLink($typeid);\r\n\r\nTypeLink类的构造函数没有经过过滤,直接带入了sql语句.\r\n\r\nclass TypeLink \r\n{ \r\n    var $typeDir; \r\n    var $dsql; \r\n    var $TypeID; \r\n    var $baseDir; \r\n    var $modDir; \r\n    var $indexUrl; \r\n    var $indexName; \r\n    var $TypeInfos; \r\n    var $SplitSymbol; \r\n    var $valuePosition; \r\n    var $valuePositionName; \r\n    var $OptionArrayList; \r\n\r\n    //构造函数/////// \r\n    //php5构造函数 \r\n    function __construct($typeid) \r\n    { \r\n        $this->indexUrl = $GLOBALS[\'cfg_basehost\'].$GLOBALS[\'cfg_indexurl\']; \r\n        $this->indexName = $GLOBALS[\'cfg_indexname\']; \r\n        $this->baseDir = $GLOBALS[\'cfg_basedir\']; \r\n        $this->modDir = $GLOBALS[\'cfg_templets_dir\']; \r\n        $this->SplitSymbol = $GLOBALS[\'cfg_list_symbol\']; \r\n        $this->dsql = $GLOBALS[\'dsql\']; \r\n        $this->TypeID = $typeid; \r\n        $this->valuePosition = ”; \r\n        $this->valuePositionName = ”; \r\n        $this->typeDir = ”; \r\n        $this->OptionArrayList = ”; \r\n\r\n        //载入类目信息 \r\n\r\n        <font color=”Red”>$query = “SELECT tp.*,ch.typename as \r\nctypename,ch.addtable,ch.issystem FROM `#@__arctype` tp left join \r\n`#@__channeltype` ch \r\n        on ch.id=tp.channeltype  WHERE tp.id=’$typeid’ “;</font> //注射漏洞发生在这里,很明显需要magic_quotes_gpc = Off 鸡肋了吗?好可以吧至少不需要会员中心阿 \r\n\r\n        if($typeid > 0) \r\n  