INSERT INTO `web_vul_list` VALUES ('6676', '6676', 'WordPress Accept Signups插件\"email\"参数跨站脚本执行漏洞', '1', '跨站脚本攻击', '客户端攻击类型', '3', '', 'MED', 'WordPress是一种使用PHP语言和MySQL数据库开发的Blog（博客、网志)引擎，用户可以在支持PHP和MySQL数据库的服务器上建立自己的Blog。\r\nWordPress的Accept Signups插件在实现上存在跨站脚本执行漏洞，远程攻击者可利用此漏洞在受影响站点用户浏览器中执行任意代码，窃取基于Cookie的验证凭证。\r\n软件的accept-signups_submit.php脚本没下正确检查过滤email参数，攻击者可以在输入数据中插入恶意脚本代码。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('6677', '6677', 'Informix Webdriver远程管理界面可匿名访问漏洞', '1', '越权访问', '认证类型', '3', '', 'MED', 'Infomix是一个著名的数据库产品，Informix Webdriver是Informix数据库使用的Web与数据库之间的接口。\r\nInformix Webdriver 1.0存在一个漏洞允许对系统管理的非授权远程访问。\r\n如果Webdriver被直接调用，向服务器提交的URL中没有包含任何额外参数，那么对请求的回应形式是出现远程管理页面。这就容许远程恶意用户修改或删除数据库信息。', '', '0', '2000');
