INSERT INTO `web_vul_list` VALUES ('6686', '6686', 'Zixforum \'login.asp\'的layid变量远程SQL注入漏洞', '1', 'SQL注入', '命令执行类型', '3', '', 'HIGH', 'Zixforum是一款基于WEB的ASP编写的网络论坛程序。\r\n\r\nZixforum对用户提交给的参数缺少正确充分的过滤，远程攻击者可以利用此漏洞非授权操作数据库。\r\n\r\nZixforum的main.asp和login.asp脚本对用户提交layid参数数据缺少充分过滤，远程攻击者可以通过在输入数据中插入特定的SQL命令来非授权获取对数据库的访问。', '', '0', '2000');
