INSERT INTO `web_vul_list` VALUES ('6688', '6688', 'MySQL Commander远程文件包含漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'bitesser MySQL Commander 2.7及更早版本在实现上存在远程文件包含漏洞，可发送特制的URL请求到 ressourcen/dbopen.php 脚本，使用\"home\"参数指定远程系统上的恶意文件，导致在有漏洞的服务器上执行任意代码。\r\n\r\n存在漏洞的代码如下，可见include语句中的$home并没有被正确验证/过滤。\r\n\r\nressourcen/dbopen.php\r\n<?php\r\ninclude $home.\"ressourcen/class.systemObject.php\";\r\ninclude $home.\"ressourcen/class.DatabaseMysql.php\";\r\n$db = new DatabaseMysql($config->dbuser[$_SESSION[\'which_db\']], $config->dbpass[$_SESSION[\'which_db\']],\r\n$config->dbserver[$_SESSION[\'which_db\']]);\r\n$ok = $db->init();\r\n\r\nif (!$ok and $db->error and (strlen($config->dbserver[1]) > 0)) {\r\necho $db->getError();\r\n}\r\n\r\n?>\r\n攻击成功要求php配置register_globals=on和allow_fopenurl=on。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('6689', '6689', 'BlackBerry Enterprise Server MDS Connection Service跨站脚本漏洞漏洞', '1', '跨站脚本攻击', '客户端攻击类型', '3', '', 'MED', 'BlackBerry Enterprise Server软件是BlackBerry企业解决方案中的一个组件，能够在无线设备、企业应用程序和无线网络之间起到中间连接的作用。\r\n\r\nBlackBerry Enterprise Server所使用MDS Connection Service的/admin/statistics/ConfigureStatistics脚本没有正确地验证多个参数输入，远程攻击者可以通过向该脚本提交恶意请求执行跨站脚本攻击，导致在用户浏览器会话中执行任意HTML和脚本代码。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('6690', '6690', 'VTiger CRM多个输入验证漏洞', '1', '跨站脚本攻击', '客户端攻击类型', '3', '', 'MED', 'tiger是一款由一家印度公司维护的开放源码客户端关系管理系统（CRM）。\r\n\r\n由于没有正确的过滤用户输入，导致vtiger CRM中存在多个输入验证错误，具体如下：\r\nCRM中存在多个跨站脚本漏洞，例如，恶意的本地用户可以创建字段中包含有脚本代码的联系人、帐号或其他数据集，然后将该联系人链接发送给管理员，这样脚本代码就可能获得权限提升。', '', '0', '2000');
