INSERT INTO `web_vul_list` VALUES ('6693', '6693', 'phpBB2 highlight变量二次解码导致远程命令执行漏洞', '1', 'SQL注入', '命令执行类型', '3', '', 'HIGH', 'phpBB是一款由PHP编写的WEB论坛应用程序，支持多种数据库系统，可使用在多种Unix和Linux操作系统下。\r\n\r\nphpBB对highlight变量不正确的二次解码导致远程攻击者可以注入PHP代码执行系统命令。\r\n\r\n由于phpBB的URL解码会把%2527转换成%27(单引号)，并没有追加\'\\\\\'号的，因此可以注入PHP代码执行系统命令。', '', '0', '2000');
