INSERT INTO `web_vul_list` VALUES ('6695', '6695', 'Advanced GuestBook addentry.php远程文件包含漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'Advanced GuestBook是一款流行的PHP论坛程序phpBB的组件。\r\nAdvanced GuestBook实现上存在输入验证漏洞，远程攻击者可能利用此漏洞在服务器上以Web进程权限执行任意命令。\r\nAdvanced GuestBook的addentry.php脚本没有对phpbb_root_path变量值的内容做充分的过滤就在脚本中引用，导致攻击者可以使之包含远程机器上的PHP脚本进行执行。', '', '0', '2000');
