INSERT INTO `web_vul_list` VALUES ('6875', '6875', 'SQuery gore.php远程文件包含漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'Query是一个基于Web的用PHP实现的网络游戏服务器及用户管理模块。\r\n\r\nSQuery的实现上存在输入验证漏洞，远程攻击者可能利用此漏洞在服务器上以Web进程权限执行任意命令。\r\n\r\nSQuery的gore.php脚本对用户提交的libpath参数没有做充分的检查过滤，攻击者可能利用此漏洞使gore.php脚本包含远程服务器上的PHP代码执行。', '', '0', '2000');
