INSERT INTO `web_vul_list` VALUES ('6989', '6989', 'HolaCMS htmltags.php本地文件包含漏洞', '1', '信息泄露', '信息泄露类型', '3', '', 'MED', 'HOLACMS是一款基于WEB的内容管理系统。\r\n\r\nHOLACMS包含的htmltags.php脚本没有正确过滤用户提交的包含文件参数，远程攻击者可以利用这个漏洞访问包含用户验证信息的文件 。\r\n\r\nhtmltags.php脚本用于编辑内部文件，问题是htmltags.php没有充分检查访问者是否为管理员，并且对要编辑的文件缺少充分检查，攻击者可以直接请求包含密码文件档，进行编辑或者直接获得管理员密码对应用服务程序进行控制。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('6990', '6990', 'webpage.cgi泄漏敏感信息漏洞', '1', '信息泄露', '信息泄露类型', '3', '', 'MED', 'webpage.cgi存在一个漏洞。当URL请求的文件不存在时，webpage.cgi会向客户端浏\r\n览器返回某些敏感信息，比如脚本所在路径、HTTP根目录所在路径、Perl版本、\r\nserver_admin、server_name、PATH环境变量等。这为进一步的攻击做了准备。', '', '0', '2000');
