INSERT INTO `web_vul_list` VALUES ('7179', '7179', 'GetSolutions GetIntranetSQL注入漏洞', '1', 'SQL注入', '命令执行类型', '3', '', 'HIGH', 'getIntranet是一款Intranet基于WEB的实时管理信息，文档内容等的系统。\r\n\r\ngetIntranet多个脚本对用户提交的参数输入缺少充分过滤，远程攻击者可以利用这个漏洞进行SQL注入攻击，可能更改数据库或获得敏感信息。\r\n\r\ngetIntranet包含的多个脚本对参数缺少过滤，攻击者提交包含恶意SQL代码的数据，可更改原来数据库逻辑，可能更改数据库或获得敏感信息。', '', '0', '2000');
