INSERT INTO `web_vul_list` VALUES ('7200', '7200', 'Woltlab Burning Board存在跨站脚本攻击', '1', '跨站脚本攻击', '客户端攻击类型', '3', '', 'MED', 'WoltLab Burning Board是一款可自定义的论坛程序。 \r\n\r\nWoltLab Burning Board没有执行任何有效性检查便允许用户通过HTTP请求执行某些操作，如果登录用户访问了恶意网页便可以将保密消息移动到回收站。\r\n\r\nWoltLab Burning Board的wcf/acp/dereferrer.php模块没有验证对url参数的输入便返回给了用户，远程攻击者可以通过提交恶意请求导致在用户浏览器会话中执行任意HTML和脚本代码。', '', '0', '2000');
