INSERT INTO `web_vul_list` VALUES ('7206', '7206', 'Phorum follow.php脚本远程SQL注入漏洞', '1', 'SQL注入', '命令执行类型', '3', '', 'HIGH', 'Phorum是一款基于PHP的WEB论坛程序。\r\n\r\nPhorum包含的\'follow.php\'脚本不正确过滤用户提交的URL数据，远程攻击者可以利用这个漏洞进行SQL注入攻击。\r\n\r\n\'follow.php\'对\"$thread\" 参数缺少充分过滤，攻击者提交包含恶意SQL命令的数据作为此参数数据，可更改原来的SQL逻辑，获得敏感信息，如管理员的密码HASH数据。', '', '0', '2000');
