INSERT INTO `web_vul_list` VALUES ('7209', '7209', 'Miva htmlscript远程察看任意文件漏洞', '1', 'SQL注入', '命令执行类型', '3', '', 'HIGH', 'Miva htmlscript是Miva Script的前身，一种跨平台的CGI脚本语言，由Miva.com开发。\r\n\r\n某些老版本的htmlscript存在漏洞，远程攻击者可以利用此漏洞以Web服务进程的权限查看主机上的任意文件。\r\n\r\nhtmlscript2.99x或者更早版本，由于脚本没有过滤用户输入中可能夹带的“../”字串，远程攻击者可能以httpd进程的权限来遍历主机上的目录，察看任意有权限读取的文件。', '', '0', '2000');
