INSERT INTO `web_vul_list` VALUES ('7302', '7302', 'Mambo/Joomla plugin.class.php脚本远程文件包含漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'Mambo（也被称为Joomla）是一款开放源代码的WEB内容管理系统。\r\n\r\nMambo在处理用户请求时存在输入验证漏洞，远程攻击者可能利用此漏洞在服务器上以Web进程权限执行任意命令。\r\n\r\n在Mambo的com_comprofiler文件夹的plugin.class.php脚本中：\r\nplugin.class.php\r\n<?php\r\n/**\r\n* Plugin handler\r\n* @package Joomla\r\n* @author various, JoomlaJoe and Beat\r\n*/\r\n\r\nrequire_once( $mosConfig_absolute_path . \'/includes/domit/xml_domit_lite_include.php\' \\\\\r\n);\r\n由于没有正确的过滤$mosConfig_absolute_path变量，导致了远程文件包含漏洞。攻击者可以利用这个漏洞执行任意代码。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7303', '7303', 'Verity\'s Search`97查看任意文件漏洞', '1', '信息泄露', '信息泄露类型', '3', '', 'MED', 'Verity\'s Search`97是Verity公司搜索引擎的Web访问界面。\r\n\r\nVerity\'s Search`97的search97.vts脚本未对用户如入做充分过滤，远程攻击者可能利用此漏洞进行目录遍历攻击，导致系统文件泄漏。\r\n\r\n软件包中的search97.vts对用户输入的“..”未做充分过滤，攻击者可能通过构造特殊的请求来读取任何服务器上任何Web服务进程有读权限的文件。', '', '0', '2000');
