INSERT INTO `web_vul_list` VALUES ('7353', '7353', 'ComVironment grab_globals.lib.php脚本远程文件包含漏洞', '1', 'SQL注入', '命令执行类型', '3', '', 'HIGH', 'ComVironment是一种开源的基于PHP的Web内容管理系统。\r\n\r\nComVironment的实现上存在输入验证漏洞，远程攻击者可能利用此漏洞在服务器上以Web进程权限执行任意命令。\r\n\r\nComVironment的grab_globals.lib.php脚本没有正确验证inc_dir参数的输入，允许攻击者通过包含本地或外部资源任意文件导致执行任意PHP代码。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7354', '7354', 'PSInclude远程任意命令执行漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'psInclude是一个类似SSI #INCLUDE的脚本。\r\n\r\npsInclude不正确过滤用户提交给\"template\"的数据，远程攻击者可以利用这个漏洞以WEB权限在系统上执行任意命令。\r\n\r\n由于对\"template\"参数缺少充分过滤，攻击者提交SHELL元字符及命令的数据给这个参数，可能以WEB进程权限在系统上执行任意命令。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7355', '7355', 'Trlinux Postaci Webmail 口令泄漏问题', '1', '信息泄露', '信息泄露类型', '3', '', 'MED', 'Postaci Webmail是一个数据库驱动的基于web的邮件系统。它在缺省配置的情\r\n况下可能允许远程攻击者获取对数据库系统的访问权限。\r\n\r\nPostaci Webmail缺省将数据库的用户名和口令信息保存在global.inc文件中，\r\n这个文件是“全体可读”的并且保存在web浏览器可访问的目录中。因此，攻击\r\n者可以通过请求global.inc文件来直接获得数据库用户名和口令。从而攻击者\r\n可能访问系统数据库并可能进一步入侵操作系统。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7356', '7356', 'TML CMS \'index.php\'SQL注入漏洞', '1', 'SQL注入', '命令执行类型', '3', '', 'HIGH', 'TML是一款新型的内容管理系统。\r\n\r\nTML CMS中存在多个输入验证漏洞，可能允许远程攻击者执行各种跨站脚本或SQL注入攻击。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7357', '7357', 'X-Stat PHPInfo可导致信息泄露漏洞', '1', '信息泄露', '信息泄露类型', '3', '', 'MED', 'X-Stat是一款PHP语言编写免费的WEB通信分析程序，可以运行在Unix和Linux操作系统下，也可运行在Microsoft Windows操作系统平台下。\r\n\r\nX-Stat由于对部分错误WEB请求处理存在问题可导致相关主机信息泄露。\r\n\r\n攻击者可以对X-Stat系统中的x_stat_admin.php脚本参数action提交phpinfo变量，可导致相关主机信息泄露给攻击者，包括主机信息，操作系统信息和服务器信息等。\r\n\r\n攻击者可以通过这些信息，对目标系统进行进一步的攻击。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7358', '7358', 'NConf \'id\' 参数多个SQL注入漏洞', '1', 'SQL注入', '命令执行类型', '3', '', 'HIGH', 'NConf是配置Nagios网络监控系统的开源工具。\r\n\r\nNconf 1.3及其他版本多个脚本的id参数存在sql注入漏洞，成功利用这些漏洞可允许攻击者执行未授权数据库操作。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7359', '7359', 'Koch Roland Rolis Guestbook $path远程文件包含漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'Rolis Guestbook是一款基于WEB的留言簿。\r\n\r\nRolis Guestbook没有充分过滤用户提交的URI参数，远程攻击者可以利用这个漏洞包含远程服务器上的恶意文件，以WEB进程权限执行任意代码。\r\n\r\n问题存在于insert.inc.php文件中：\r\n\r\n<?php\r\n  include ($path . \"data.inc.php\");\r\n  include ($path . \"header.inc.php\");\r\n  include($path . \"connection_data.inc.php\");\r\n  [ skip ]\r\n\r\n由于对用户提交的$path参数缺少充分过滤，如果指定远程服务器上的恶意文件作为包含文件，可导致以WEB进程权限执行任意PHP代码。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7360', '7360', 'REDAXO REX[INCLUDE_PATH]参数‘index.inc’文件包含漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'REDAXO是一款德文的内容管理系统。\r\n\r\nREDAXO没有正确的过滤用户提交给redaxo/include/addons/version/pages/index.inc.php和redaxo/include/addons/import_export/pages/index.inc.php页面的REX[INCLUDE_PATH]参数便用于包含文件，远程攻击者可以通过包含本地或外部资源的文件来执行任意PHP代码。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7361', '7361', 'REDAXO REX\'specials.inc.php\'的[INCLUDE_PATH]参数远程包含漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'REDAXO是一款德文的内容管理系统。\r\n\r\nREDAXO没有正确的过滤用户提交给redaxo/include/addons/version/pages/index.inc.php和redaxo/include/addons/import_export/pages/index.inc.php页面的REX[INCLUDE_PATH]参数便用于包含文件，远程攻击者可以通过包含本地或外部资源的文件来执行任意PHP代码。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7362', '7362', 'MKPortal 存在SQL注入漏洞', '1', 'SQL注入', '命令执行类型', '3', '', 'HIGH', 'MKPortal是一款免费的主站/内容管理系统，可无缝的集成于大多数流行的论坛软件。\r\n\r\nMKPortal的index.php文件的ind参数存在SQL注入漏洞。此外，pm_popup.php的u1、m1、m2、m3、m4参数中没有正确过滤用户输入，存在跨站脚本漏洞。攻击者可以利用这些漏洞远程执行任意代码。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7363', '7363', 'MKPortal跨站脚本漏洞', '1', '跨站脚本攻击', '客户端攻击类型', '3', '', 'MED', 'MKPortal是一款免费的主站/内容管理系统，可无缝的集成于大多数流行的论坛软件。\r\n\r\nMKPortal的index.php文件的ind参数存在SQL注入漏洞。此外，pm_popup.php的u1、m1、m2、m3、m4参数中没有正确过滤用户输入，存在跨站脚本漏洞。攻击者可以利用这些漏洞远程执行任意代码。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7364', '7364', 'Allaire ColdFusion 4.0x CFCACHE功能泄露信息漏洞', '1', '信息泄露', '信息泄露类型', '3', '', 'MED', 'Allaire ColdFusion是一个用来增强Web交互功能的组件。\r\n\r\nColdFusion 4.x包含一个叫做CFCACHE的功能，它通过储存HTML处理CFM页的输出来提高服务器的性能。\r\n\r\n当CFCACHE标签处理CFM页面时，它创建一些临时文件，其中一些文件是.tmp文件，其中包含了当前的HTML输出。同时它也创建一个名为cfcache.map的文件，其中包含了到.tmp文件的指针，内容有绝对路径、时间信息和其他URL信息。这些信息如果暴露出来可以造成潜在的危害。\r\n\r\n这些文件都放在同一个目录，并且可以通过外部的URL进行远程访问。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7365', '7365', 'IBM Rational RequisitePro ReqWebHelp组件\'workingSet.jsp\'跨站脚本漏洞', '1', 'SQL注入', '命令执行类型', '3', '', 'HIGH', 'IBM Rational RequisitePro是一个强大、易用、集成的需求管理产品。\r\n\r\nRational RequisitePro所使用的ReqWebHelp组件没有正确地验证提交给/ReqWebHelp/basic/searchView.jsp页面的searchWord、maxHits、scopedSearch和scope参数以及提交给/ReqWebHelp/advanced/workingSet.jsp页面的operation参数便返回给了用户，远程攻击者可以通过恶意的HTTP请求执行跨站脚本攻击，导致在用户浏览器会话中执行任意HTML和脚本代码。', '', '0', '2000');
