INSERT INTO `web_vul_list` VALUES ('7367', '7367', 'ACal Calendar day.php远程文件包含漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'ACal Calendar是一款基于Web的行事日历管理软件。\r\n\r\nACal Calendar在处理用户请求时存在输入验证漏洞，远程攻击者可能利用此漏洞在服务器上以Web进程权限执行任意命令。\r\n\r\nACal Calendar的day.php脚本中没有正确验证path参数的输入，允许攻击者通过包含本地或外部资源任意PHP代码导致执行任意命令。', '', '0', '2000');
