INSERT INTO `web_vul_list` VALUES ('7369', '7369', 'RaXnet Cacti top_graph_header.php远程文件包含漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'Cacti是一款轮循数据库（RRD）工具，可帮助从数据库信息创建图形，有多个Linux版本。\r\n\r\nCacti的top_graph_header.php脚本中存在输入验证错误，允许攻击者包含远程站点的任意PHP代码，这可能导致以Web Server进程的权限执行任意命令。\r\n\r\n漏洞起因是脚本盲目信任用户提供的library_path变量。', '', '0', '2000');
INSERT INTO `web_vul_list` VALUES ('7370', '7370', 'ChangshinSoft ezTrans Server远程目录遍历漏洞', '1', '目录遍历', '信息泄露类型', '3', '', 'MED', 'ChangshinSoft EZTrans Server是一款在线日韩互译系统。\r\n\r\nChangshinSoft EZTrans包含的下载模块对用户输入缺少充分过滤，远程攻击者可以利用这个漏洞以WEB权限在系统上查看任意文件内容。\r\n\r\nEZTrans包含的download.php脚本对用户提交给\"filename\"参数的数据缺少充分过滤，远程攻击者提交提交包含多个\"../\"字符的恶意数据给此变量，可能以WEB进程权限在系统上查看任意文件内容。', '', '0', '2000');
