INSERT INTO `web_vul_list` VALUES ('7381', '7381', 'BadBlue 源代码泄漏漏洞', '1', '信息泄露', '信息泄露类型', '3', '', 'MED', 'BadBlue 是一个运行在Microsoft Windows系统下的小型基于web的文件共享工具。\r\n\r\n它的1.02版中存在一个安全问题，由于没有过滤web请求中的一些特殊字符,可能导致泄漏\r\n文件内容。攻击者只需在正常的文件请求后面增加一个%00字符，就可以获取此文件的内容。', '', '0', '2000');
