INSERT INTO `web_vul_list` VALUES ('7415', '7415', 'Free Simple CMS\'meta\'参数远程文件包含漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'Free Simple CMS是一款开源的内容管理系统。\r\n\r\nFree Simple CMS没有正确地过滤提交给themes/default/index.php页面的meta、phpincdir、body、footer、header、menu_left和menu_right参数便用于包含文件，远程攻击者可以通过通过包含本地或远程资源导致执行任意代码。成功利用这个漏洞要求打开了register_globals。', '', '0', '2000');
