INSERT INTO `web_vul_list` VALUES ('7458', '7458', 'PHP-Ping \"count\"参数任意命令执行漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'PHP-Ping是一款基于WEB的PING程序实现。\r\n\r\nPHP-Ping对用户提交的参数缺少充分过滤，远程攻击者可以利用这个漏洞以WEB进程权限在系统上执行任意命令。\r\n\r\n主要漏洞是由于不正确调用system()调用，程序对用户提交给\"$count\"变量的数据缺少充分过滤，提交包含SHELL命令的数据作为参数给PHP-Ping，可导致以WEB进程权限在系统上执行任意命令。', '', '0', '2000');
