INSERT INTO `web_vul_list` VALUES ('7478', '7478', 'Wordpress Disclosure Policy 1.0远程文件包含漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'WordPress是一种使用PHP语言开发的博客平台，用户可以在支持PHP和MySQL数据库的服务器上架设自己的网志。 \r\n\r\nWordPress的Disclosure Policy插件中存在远程文件包含漏洞，远程攻击者可能利用此漏洞在服务器上执行任意命令。\r\n\r\n该漏洞源于应用程序没有充分过滤用户提供的输入，远程攻击者可借助abspath参数进行攻击。', '', '0', '2000');
