INSERT INTO `web_vul_list` VALUES ('7510', '7510', 'CPCommerce远程文件包含漏洞', '1', '系统命令执行', '命令执行类型', '3', '', 'HIGH', 'e-commerce是一款开放源代码的电子商务解决方案。\r\n\r\ne-commerce包含的_functions.php脚本可包含远程文件，远程攻击者可以利用这个漏洞以WEB进程权限在系统上执行任意命令。\r\n\r\n_functions.php包含如下有问题代码：\r\n\r\n  require_once(\"{$prefix}_config.php\");\r\n  require_once(\"{$prefix}_gateways.php\");\r\n\r\n由于对$prefix变量缺少充分过滤，攻击者可以指定远程服务器上的文件作为包含文件，可导致以WEB权限执行攻击者提供的恶意PHP命令。', '', '0', '2000');
