INSERT INTO `cgidb` VALUES ('7171', null, 'SQL注入', '/wp-content/plugins/hitasoft_player/config.php?id=1%20union%20all%20select  %201,2,3,4,5,6,7,8,md5(333),10,11,12,13,14,15,16,17 from wp_users--', 'GET', '310dcbbf4cce62f762a2aaa148d556bd', '2', null, null, null, null, 'WordPress是一种使用PHP语言和MySQL数据库开发的Blog（博客、网志)引擎，用户可以在支持PHP和MySQL数据库的服务器上建立自己的Blog。\r\n\r\nWordPress的FLV Player插件1.1及其他版本存在SQL注入漏洞，通过\"id\"参数发送到wp-content/plugins/hitasoft_player/config.php的输入没有正确过滤即被用在SQL查询中，通过注入SQL代码，可导致操作SQL查询。', null, null, '1', '6927', '补丁：http://wordpress.org/');
INSERT INTO `cgidb` VALUES ('7172', null, '跨站脚本攻击', '/map.php?lat=%3C/script%3E%3Cscript%3Ealert(111)%3C/script%3E', 'GET', 'script>alert(111)</script>#nvs_test#', '2', null, null, null, null, 'LiveZilla是新一代的在线客服与客户地理位置跟踪系统。\r\n\r\nLiveZilla没有正确地过滤提交给map.php页面的lat、lng和zom参数便返回给了用户，远程攻击者可以通过提交恶意参数请求执行跨站脚本攻击，导致在用户浏览器会话中执行任意HTML和脚本代码。以下是有漏洞的代码段：\r\n\r\n1. livezilla/templates/map.tpl（18-20行）\r\nvar default_lat = <!--dlat-->; \r\nvar default_lng = <!--dlng-->; \r\nvar default_zom = <!--dzom-->;\r\n\r\n2. livezilla/map.php（15-28行）\r\nif(isset($_GET[\"lat\"])) \r\n$map = str_replace(\"<!--dlat-->\",$_GET[\"lat\"],$map); \r\nelse \r\n$map = str_replace(\"<!--dlat-->\",\"25\",$map); \r\n\r\nif(isset($_GET[\"lng\"])) \r\n$map = str_replace(\"<!--dlng-->\",$_GET[\"lng\"],$map); \r\nelse \r\n$map = str_replace(\"<!--dlng-->\",\"10\",$map); \r\n\r\nif(isset($_GET[\"zom\"])) \r\n$map = str_replace(\"<!--dzom-->\",$_GET[\"zom\"],$map); \r\nelse \r\n$map = str_replace(\"<!--dzom-->\",\"1\",$map);', null, null, '1', '6928', '补丁：http://www.livezilla.net/home/en/');
INSERT INTO `cgidb` VALUES ('7173', null, '系统命令执行', '/cgi-bin/cgiforum.pl?thesection=../../../../../../etc/passwd%00', 'GET', '/bin/(bash|sh)[^\\r\\n<>]*[\\r\\n]', '3', null, null, null, null, 'DC Scripts DCForum是一个商业版CGI脚本，用于在线WWW方式讨论。\r\n\r\nDCForum实现上存在输入验证漏洞，远程攻击者可以利用此漏洞遍历服务器目录。\r\n\r\nDC Scripts DCForum未能正确检查来自用户输入的\"thesection\"变量值，利用\"../\"攻击方式，远程攻击者可以利用一个精心准备的URL请求导致脚本遍历服务器根目录，进而获取敏感信息。所能访问的文件取决于Web服务器当前启动的用户身份，一般是nobody。', null, null, '1', '6929', '补丁：http://freshmeat.net/projects/cgiforum/');
