INSERT INTO `cgidb` VALUES ('7332', null, '跨站脚本攻击', '/inventory/consulta_fact.php?fact_num=<script>alert(1)</script>', 'GET', '<script>alert(1)</script>#nvs_test#', '2', null, null, null, null, 'Inventory是基于PHP和SQL的存货跟踪系统。\r\n\r\nInventory 1.0和其他版本存在安全漏洞，成功利用这些漏洞可允许攻击者窃取Cookie身份验证凭证、控制应用、访问或修改数据、利用下层数据库内的其他漏洞。', null, null, '1', '7088', '补丁：https://github.com/farevalod/inventory');
INSERT INTO `cgidb` VALUES ('7333', null, '信息泄露', '/cgi-bin/wrap?/../../../../../etc', 'GET', '10000', '1', null, null, null, null, 'Wrap 是SGI IRIX操作系统的Outbox环境子系统的一个文件，对于IRIX 6.2以上版本是默认被安装的，6.2以前版本是可选安装的。\r\n\r\nWrap实现上存在输入验证漏洞，远程攻击者可以利用此漏洞列出任意目录的文件列表。\r\n\r\nIRIX 系统所带的/cgi-bin/wrap程序没有正确检查用户输入中所包含的“../”字符，这可能导致攻击者获取任意可读目录的内容列表。例如：', null, null, '1', '7089', '补丁：http://www.sgi.com/support/security/advisories.html');
