INSERT INTO `cgidb` VALUES ('7451', null, '系统命令执行', '/manager/static/view.php?propID=0&INC=@RFIURL', 'GET', '@RFIURL_RESPONSE', '2', null, null, null, null, 'phpRealty是基于PHP/MYSQL的内容管理软件。 \r\n\r\nphpRealty的manager/static/view.php文件中没有正确地过滤INC参数，远程攻击者可以通过包括本地或远程资源导致执行任意代码。\r\n\r\n相关的代码如下：\r\n\r\n11:    if(!isset($_GET[\'propID\']) || !is_numeric($_GET[\'propID\']) || empty($_GET[\'propID\'])){\r\n13:    return;\r\n\r\n\r\n17:    include($INC.\"curr_conv.class.php\");   // -------->>> Vuln Line!!\r\n                                          // the var $INC isn\'t declared', null, null, '1', '7207', '补丁：http://php-realty.com/home.html');
INSERT INTO `cgidb` VALUES ('7452', null, '跨站脚本攻击', '/wp-content/plugins/tagninja/fb_get_profile.php?id=<script>alert(333)</script>', 'GET', '<script>alert(333)</script>#nvs_test#', '2', null, null, null, null, 'WordPress是一款免费的论坛Blog系统。\r\n\r\nWordPress的TagNinja插件\"id\"参数在实现存在跨站脚本执行漏洞，远程攻击者可利用此漏洞在受影响站点执行任意代码，窃取Cookie凭证。', null, null, '1', '7208', '补丁：http://wordpress.org/');
INSERT INTO `cgidb` VALUES ('7453', null, 'SQL注入', '/cgi-bin/htmlscript?../../../../etc/passwd', 'GET', '/bin/(bash|sh)[^\\r\\n<>]*[\\r\\n]', '3', null, null, null, null, 'Miva htmlscript是Miva Script的前身，一种跨平台的CGI脚本语言，由Miva.com开发。\r\n\r\n某些老版本的htmlscript存在漏洞，远程攻击者可以利用此漏洞以Web服务进程的权限查看主机上的任意文件。\r\n\r\nhtmlscript2.99x或者更早版本，由于脚本没有过滤用户输入中可能夹带的“../”字串，远程攻击者可能以httpd进程的权限来遍历主机上的目录，察看任意有权限读取的文件。', null, null, '1', '7209', '补丁：http://www.miva.com/');
