INSERT INTO `cgidb` VALUES ('7598', null, '系统命令执行', '/cgi-bin/psinclude.cgi?template=|id%20-a;uptime|', 'GET', 'uid=[0-9]{0,20}\\(.{0,20}\\)\\sgid=[0-9]{0,20}\\(.{0,20}\\)\\sgroups=[0-9]{0,20}\\(.{0,20}\\)', '3', null, null, null, null, 'psInclude是一个类似SSI #INCLUDE的脚本。\r\n\r\npsInclude不正确过滤用户提交给\"template\"的数据，远程攻击者可以利用这个漏洞以WEB权限在系统上执行任意命令。\r\n\r\n由于对\"template\"参数缺少充分过滤，攻击者提交SHELL元字符及命令的数据给这个参数，可能以WEB进程权限在系统上执行任意命令。', null, null, '1', '7354', '补丁：http://www.psyon.org/projects/psinclude/psinclude142.zip');
INSERT INTO `cgidb` VALUES ('7599', null, '信息泄露', '/includes/global.inc', 'GET', '10000', '1', null, null, null, null, 'Postaci Webmail是一个数据库驱动的基于web的邮件系统。它在缺省配置的情\r\n况下可能允许远程攻击者获取对数据库系统的访问权限。\r\n\r\nPostaci Webmail缺省将数据库的用户名和口令信息保存在global.inc文件中，\r\n这个文件是“全体可读”的并且保存在web浏览器可访问的目录中。因此，攻击\r\n者可以通过请求global.inc文件来直接获得数据库用户名和口令。从而攻击者\r\n可能访问系统数据库并可能进一步入侵操作系统。', null, null, '1', '7355', '');
