INSERT INTO `cgidb` VALUES ('7642', null, 'SQL注入', '/board.php?FID=2%27', 'GET', 'SQL syntax', '2', null, null, null, null, 'OpenBB是一款由PHP和MySQL数据库结合编写的论坛程序。\r\n\r\nOpenBB包含的\'board.php\'脚本对输入缺少充分检查，远程攻击者可以利用这个漏洞插入任意SQL命令到数据，导致信息泄露，数据破坏。\r\n\r\nboard.php脚本对FID参数过滤不充分，攻击者只要在参数数字后增加空格值，就可以在后面追加相关的SQL命令，注入恶意SQL命令可以导致数据库信息泄露，或破坏数据库。', null, null, '1', '7398', '补丁：http://www.openbb.co.uk/');
