INSERT INTO `cgidb` VALUES ('7690', null, '系统命令执行', '/help.php?css_path=@RFIURL', 'GET', '@RFIURL_RESPONSE', '2', null, null, null, null, 'PHP Live!是一种构架于PHP、MySQL之上的开源软件，可为企业用户提供实时交谈服务。\r\n\r\nPHP Live!没有正确过滤对help.php和setup/header.php文件的css_path参数的输入，允许远程攻击者通过包含本地和外部资源导致执行任意PHP代码。\r\n\r\n漏洞代码如下：\r\n\r\n<? $css_path = ( !isset( $css_path ) ) ? $css_path = \"./\" : $css_path ; include_once( $css_path.\"css/default.php\" ) ; ?>', null, null, '1', '7446', '补丁：http://www.phplivesupport.com/');
